from google.oauth2.credentials import Credentialsfrom google_auth_oauthlib.flow import InstalledAppFlowfrom googleapiclient.discovery import build# Scopes required to access Google Fit APISCOPES = ['https://www.googleapis.com/auth/fitness.heart_rate.read',          'https://www.googleapis.com/auth/fitness.activity.read']def get_google_fit_service():    # Load OAuth 2.0 credentials from file    creds = None    flow = InstalledAppFlow.from_client_secrets_file(        'client_secret.json', SCOPES)    creds = flow.run_local_server(port=8080)    # Build Google Fit service    service = build('fitness', 'v1', credentials=creds)    return service