import sqlite3import osDB_PATH = os.path.join(os.path.dirname(__file__), "../../vitals.db")def init_db():    conn = sqlite3.connect(DB_PATH)    cursor = conn.cursor()    cursor.execute('''        CREATE TABLE IF NOT EXISTS vitals (            id INTEGER PRIMARY KEY AUTOINCREMENT,            heart_rate INTEGER,            blood_pressure INTEGER,            alert TEXT,            timestamp DATETIME DEFAULT CURRENT_TIMESTAMP        )    ''')    conn.commit()    conn.close()def save_vitals(heart_rate, blood_pressure, alert):    conn = sqlite3.connect(DB_PATH)    cursor = conn.cursor()    cursor.execute('''        INSERT INTO vitals (heart_rate, blood_pressure, alert)        VALUES (?, ?, ?)    ''', (heart_rate, blood_pressure, alert))    conn.commit()    conn.close()def get_latest_vitals():    conn = sqlite3.connect(DB_PATH)    cursor = conn.cursor()    cursor.execute('''        SELECT heart_rate, blood_pressure, alert, timestamp        FROM vitals        ORDER BY timestamp DESC        LIMIT 1    ''')    row = cursor.fetchone()    conn.close()    if row:        return {            "heart_rate": row[0],            "blood_pressure": row[1],            "alert": row[2],            "timestamp": row[3]        }    return {"error": "No vitals found"}