# /Users/harsha/Predictive-Healing-Pavilion/api/vitals/routes.pyfrom flask import jsonify, request, appfrom .database import save_vitalsfrom .twilio_integration import send_sms_alert# Example threshold values for alertHEART_RATE_THRESHOLD = 140  # Example threshold for heart rateBLOOD_PRESSURE_THRESHOLD = 150  # Example threshold for blood pressure@app.route('/api/vitals', methods=['POST'])def handle_vitals():    try:        data = request.get_json()        heart_rate = data['heart_rate']        blood_pressure = data['blood_pressure']        # Determine if alert is necessary        alert = "All Good"        if heart_rate > HEART_RATE_THRESHOLD:            alert = "Heart Rate too high!"        elif blood_pressure > BLOOD_PRESSURE_THRESHOLD:            alert = "Blood Pressure too high!"        # Save vitals data into the database        save_vitals(heart_rate, blood_pressure, alert)        # Send SMS if alert is triggered        if alert != "All Good":            message = f"Alert: {alert} | Heart Rate: {heart_rate} bpm | Blood Pressure: {blood_pressure} mmHg"            send_sms_alert("+919556263453", message)  # Replace with recipient phone number        return jsonify({"message": "Vitals saved successfully!"}), 200    except Exception as e:        return jsonify({"error": str(e)}), 500