# /Users/harsha/Predictive-Healing-Pavilion/api/vitals/twilio_integration.pyfrom twilio.rest import Client# Twilio credentialsTWILIO_PHONE_NUMBER = "+14134792148"  # Replace with your Twilio numberTWILIO_SID = "AC699a7bb7ec99536fb29aa4ff8f263187"        # Your Twilio SIDTWILIO_AUTH_TOKEN = "6de9ea24effe6292377cdaac64cf6102"  # Your Twilio Auth Token# Client setupclient = Client(TWILIO_SID, TWILIO_AUTH_TOKEN)def send_sms_alert(to_phone_number, message):    try:        client.messages.create(            body = message,            from_ = TWILIO_PHONE_NUMBER,            to = "+919556263453" # Replace with recipient phone number        )        print(f"✅ SMS sent to {to_phone_number}")    except Exception as e:        print(f"❌ Error sending SMS: {e}")